import {
	FETCH_PHONE_START,
	FETCH_PHONE_SUCCESS,
	FETCH_PHONE_FAILURE,
	FETCH_PHONE_BY_ID_START,
	FETCH_PHONE_BY_ID_SUCCESS,
	FETCH_PHONE_BY_ID_FAILURE,
	LOAD_MORE_PHONE_START,
	LOAD_MORE_PHONE_SUCCESS,
	LOAD_MORE_PHONE_FAILURE,
	ADD_PHONE_TO_BASKET
} from 'actionTypes';
import { 
	fetchPhones as fetchPhonesApi, 
	loadMorePhones as loadMorePhonesApi,
	fetchPhoneById as fetchPhoneByIdApi 
} from 'api';
import { getRenderedPhonesLength } from 'selectors';

export const fetchPhones = () => async dispatch => {
	dispatch({type: FETCH_PHONE_START});

	try{
		const phones = await fetchPhonesApi();
		dispatch({
			type: FETCH_PHONE_SUCCESS,
			payload: phones
		});
	}catch (err){
		dispatch({
			type: FETCH_PHONE_FAILURE,
			payload: err,
			erroe: true
		});
	}
}

export const loadMorePhones = () => async (dispatch, getState) => {
	const offset = getRenderedPhonesLength(getState());
	dispatch({type: LOAD_MORE_PHONE_START});

	try{
		const phones = await loadMorePhonesApi({offset});
		dispatch({
			type: LOAD_MORE_PHONE_SUCCESS,
			payload: phones
		});
	}catch (err){
		dispatch({
			type: LOAD_MORE_PHONE_FAILURE,
			payload: err,
			erroe: true
		});
	}
}

export const fetchPhoneById = id => async dispatch => {
	dispatch({type: FETCH_PHONE_BY_ID_START});

	try{
		const phones = await fetchPhoneByIdApi(id);
		dispatch({
			type: FETCH_PHONE_BY_ID_SUCCESS,
			payload: phones
		});
	}catch (err){
		dispatch({
			type: FETCH_PHONE_BY_ID_FAILURE,
			payload: err,
			erroe: true
		});
	}
}

export const addPhoneToBasket = id => dispatch => {
	dispatch({
		type: ADD_PHONE_TO_BASKET,
		payload: id
	});
};